		console.log("M= "+ MonthNum)
		console.log("Y= "+ YearNum)
		var count = this.getDaysInMonth(MonthNum,YearNum);
		var tDays = [];
		var weekday = new Array(7);
		weekday[0] = "S";
		weekday[1] = "M";
		weekday[2] = "T";
		weekday[3] = "W";
		weekday[4] = "T";
		weekday[5] = "F";
		weekday[6] = "S";

		for(let i=1;i<=count;i++){
			var day = new Date(YearNum,((MonthNum)-1),i).getDay();
			// console.log("day =",day);
			tDays.push(weekday[day]);
		}
		return tDays;
	}






