	// 	var weekSplit = WeekFound.split('');

	// 	var startDateOfWeek = moment(weekSplit[0]+weekSplit[1]+weekSplit[2]+weekSplit[3]).add((weekSplit[6]+weekSplit[7])-1, 'weeks').startOf('isoweek').format('DD MM YYYY');
	// 	var endDateOfWeek = moment(weekSplit[0]+weekSplit[1]+weekSplit[2]+weekSplit[3]).add((weekSplit[6]+weekSplit[7]), 'weeks').startOf('week').format('DD MM YYYY');

	// 	this.setState({
	// 		startDateOfWeek:startDateOfWeek,
	// 		endDateOfWeek:endDateOfWeek,
	// 	},()=>{
	// 		console.log("DatesOfWeek="+this.state.startDateOfWeek+" "+this.state.endDateOfWeek)
	// 	});

 //    }


	checkAll(event){
		var shiftCheck = event.target.getAttribute('data-index');
		console.log("className of sel cb="+shiftCheck)
		if(event.target.checked){
			$("."+shiftCheck).prop("checked",true);
		}else{
			$("."+shiftCheck).prop("checked",false);
		}
	}

	deselectCheckbox(event){
    	var shiftunCheck = event.target.getAttribute('data-index');
    	if (shiftunCheck) {
          var splitval  = shiftunCheck.split(' ');
          if (splitval) {

	    	console.log("event.target.checked",event.target.checked);
	    	console.log("shiftunCheck",shiftunCheck);
	    	// var checkedValue = this.props.allChecked ? true : this.state.checked
		 	if(!event.target.checked){
		 		$("#"+splitval[0]).prop("checked",false);
		 		$("#"+splitval[1]).prop("checked",false);
		 	}else{
		 		$("#"+splitval[0]).prop("checked",true);
		 		$("#"+splitval[1]).prop("checked",true);
		 	}


          }
    	}
	}

	render(){

		var emp = this.state.empID;
		var userData = ShiftSetting.find({"_id":emp}).fetch();
		var empHead = this.props.allShift[0];
		if(emp){
			if(emp.effectiveFrom==""){
				emp.effectiveFrom = "-- NA --";
			}
		}

		const data = this.props.allShift;

			return (

				<div className="row">
					<div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">

						<div className="container-fluid bg-warning">
				    		<h3><center><b>Shift Timing Setting</b></center></h3>
						</div>

						<div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 row customMargin">
				    		<p className="col-lg-2 col-md-3 col-sm-4 col-xs-4 row customMargin5"><b>Shift Planning for</b></p>
				    		<div className=" col-lg-1 col-md-1 col-sm-1 col-xs-4 input-group">
					    		<span className="input-group-addon"><i className="fa fa-calendar"></i></span>
					    		<input className="form-control" type="Month" ref="passDate" onChange={this.passDate.bind(this)}/>
					    		<input className="form-control" type="week" ref="DayFromWeek" onChange={this.DayFromWeek.bind(this)}/>
							</div>
						</div>

						<table id="table-to-xls" className="table table-bordered table-hovered table-condensed">
							<thead>
								<tr className="bg-primary"> 
									<th className="text-center" rowSpan="3"> Sr </th> 
									<th className="text-left" rowSpan="3"> Employee Name </th> 
									<th className="text-center" rowSpan="3"> SA </th> 
									{this.state.dates.map(
										(date,i) => {return (<th key={i}> {moment(date).format('DD')} </th>)}
									)}									
								</tr>
								<tr className="bg-primary"> 
									{this.getWeekDayHeader().map(

										(day,i)=>{return (<th key={i}> {day} </th>)}
									)}

								</tr>
								<tr> 
									{this.state.dates.map(
										(date,i)=>{return (<th key={i} className="checkboxContainer"> <input type="checkbox" id={"D"+i} data-index={"D"+i} onChange={this.checkAll.bind(this)} /><span className="checkmark1 text-center"></span> </th>)}
									)}

								</tr>
							</thead>

							<tbody>
								{this.props.allShift.map((emp,index)=>{
										function empid(){return index+1} ;

										return(
											<tr key={index}>
												<td> {index+1} </td>
												<td className="text-left" data-toggle="modal" data-target="#empShiftHistory" onClick={this.displayEmpShiftHistory.bind(this)} > {emp.shiftName} {emp.startTime} {emp.startTimeAmPm} </td>
												<td className="checkboxContainer"> <input type="checkbox" id={"E"+(index+1)} data-index={"E"+(index+1)} onChange={this.checkAll.bind(this)} /><span className="checkmark1 text-center"></span> </td>
													{this.state.dates.map(
														(date,dt)=>{return (<th key={dt} className="checkboxContainer"> <input type="checkbox" className={"E"+empid()+" D"+(dt+1)} align="center" name="check" data-index={"E"+empid()+" D"+(dt+1)} data-toggle="modal" data-target="#shiftChangeModal"  onChange={this.deselectCheckbox.bind(this)}/><span className="checkmark1 text-center"></span></th>)}

													)}
											</tr>
										);
									})
								}
							</tbody>
						</table>

						<table id="table-to-xls" className="table table-bordered table-hovered table-condensed">
							<thead>
								<tr className="bg-primary"> 
									<th className="text-center" rowSpan="3"> Sr </th> 
									<th className="text-left" rowSpan="3"> Employee Name </th> 
									<th className="text-center" rowSpan="3"> SA </th> 
									{this.getDateHeader().map(
										(i, index)=>{return (<th key={index}> {i} </th>)}
									)}
								</tr>
								<tr className="bg-primary"> 
									{this.getDayHeader().map(
										(i, index)=>{return (<th key={index}> {i} </th>)}
									)}
								</tr>
								<tr className="bg-primary"> 
									{this.getDateHeader().map(
										(i,index)=>{return (<th key={index} className="checkboxContainer"> <input type="checkbox" id={"D"+i} data-index={"D"+i} onChange={this.checkAll.bind(this)} /><span className="checkmark1 text-center"></span> </th>)}
									)}
								</tr>
							</thead>

							<tbody>
