import React, {Component} from 'react';
import ReactDOM from 'react-dom';

import { withTracker } from 'meteor/react-meteor-data';
import swal from 'sweetalert';

import { EmpMaster } from '/imports/shiftMgmt/shiftRoster/shiftAllotment/empMaster.js';
import "./shiftAllot.css";


class ShiftAllot extends Component{
	constructor(props){
		super(props);
		var urlEmpId = FlowRouter.getParam("empid");
		if(urlEmpId){
			var action = "Update";
		}else{
			var action = "Submit";
		}

	this.state = {
			"empId" : urlEmpId,
			"action" : action,
			"shiftName": "",
			"startTime": "",
			"startTimeAmPm": "",
			"endTime": "",
			"endTimeAmPm": "",
			"effectiveFrom": "",
			"tillDate": "",
			"empID": "",
			"Year": "",
			"Month": "",
			"isChecked": false,
			"checked": false,
			"weekdays": ""

		};
	}	

	componentWillReceiveProps(nextProps) {
  	if(!nextProps.loading){
      if(nextProps.oneEmpData){
		    this.setState({
		        "shiftName" 		: nextProps.oneEmpData.shiftName,	        
		        "startTime" 		: nextProps.oneEmpData.startTime,	        
		        "startTimeAmPm" 	: nextProps.oneEmpData.startTimeAmPm,	        
		        "endTime" 			: nextProps.oneEmpData.endTime,	        
		        "endTimeAmPm" 		: nextProps.oneEmpData.endTimeAmPm,	        
		        "effectiveFrom" 	: nextProps.oneEmpData.effectiveFrom,	   
		        "tillDate" 			: nextProps.oneEmpData.tillDate,	   

		    });
		  }
		}
	}

	getDaysInMonth(month,year) {
	  return new Date(year, month, 0).getDate();
	};

	submitBasicInfo(event){
		event.preventDefault();
		var formValues = {
			shiftName 		: this.refs.shiftName.value,
			startTime 		: this.refs.startTime.value,
			startTimeAmPm 	: this.refs.startTimeAmPm.value,
			endTime 		: this.refs.endTime.value,
			endTimeAmPm 	: this.refs.endTimeAmPm.value,
			effectiveFrom 	: this.refs.effectiveFrom.value,	
			tillDate	 	: this.refs.tillDate.value,	
		};

		if(this.state.action == "Submit"){
				Meteor.call("insertBasicInfo",formValues,
											(error,result)=>{
												if(error){
													console.log("Something went wrong! Error = ", error);
												}else{
													swal("Congrats!","Your Information Submitted Successfully.","success");
													console.log("latest id = ",result);
													FlowRouter.go("/empProfile/"+result);
													// this.setState({"inputValue":""});
													$(".modal-backdrop").remove();
												}
											});	
		}else{
			formValues._id = this.state.empId;
			Meteor.call("updateBasicInfo",formValues,
										(error,result)=>{
											if(error){
												console.log("Something went wrong! Error = ", error);
											}else{
												swal("Congrats!","Employee Profile Updated Successfully.","success");
												console.log("latest id = ",result);
												FlowRouter.go("/empProfile/"+this.state.empId);
											}
										});	
		}

	}

	handleChange(event){
		event.preventDefault();
    this.setState({
        "shiftName" 		: this.refs.shiftName.value,	        
        "startTime" 		: this.refs.startTime.value,	        
        "startTimeAmPm" 	: this.refs.startTimeAmPm.value,	        
        "endTime" 			: this.refs.endTime.value,	        
        "endTimeAmPm" 		: this.refs.endTimeAmPm.value,	        
        "effectiveFrom" 	: this.refs.effectiveFrom.value,
        "tillDate"	        : this.refs.tillDate.value,
    	});

	}


	deleteEmpProfile(event){
		event.preventDefault();
		var empId = event.currentTarget.id;

		console.log("dd = ",empId);
		swal({
		  title     : "Are you sure you want to delete?",
		  text      : "Once deleted, you will not be able to recover this record!",
		  icon      : "warning",
		  buttons   : true,
		  dangerMode: true,
		})
		.then((willDelete) => {
		  if (willDelete) {
			Meteor.call("deleteEmpProfile",empId,
								(error,result) => {
									if(error){
										swal("Something is Wrong","Contact Your System Administrator","error");
										console.log(error);
									}else{
										swal("Great!","Delete is Successful!","success");
										// FlowRouter.go("/empInfo");
									}
								}
						);
		}else {
		    swal("This record is safe!");
		  }
